import React, { useRef, useState } from "react";
import AboutPage from "./About.js";
import TeamPage from "./Ourteam.js";
import { useNavigate } from "react-router-dom"; 
import "./theme.css"; 
import "./LandingPage.css";
import watchImage from "./watch-image.png";

const LandingPage = () => {
  const aboutRef = useRef(null);
  const teamRef = useRef(null);
  const navigate = useNavigate(); // Hook for navigation
  const [menuOpen, setMenuOpen] = useState(false);

  const scrollToSection = (ref) => {
    window.scrollTo({
      top: ref.current.offsetTop - 80, // Offset for navbar
      behavior: "smooth",
    });
  };

  return (
    <div className="background">
      {/* Navbar */}
      <nav className="navbar">
        <h2 className="logo">trackfit.</h2>
        <div className={`nav-links ${menuOpen ? "show" : ""}`}>
          <button onClick={() => scrollToSection(aboutRef)}>About</button>
          <button onClick={() => scrollToSection(teamRef)}>Our Team</button>
          <button onClick={() => navigate("/login")}>Login</button>
        </div>
        <div className="menu-icon" onClick={() => setMenuOpen(!menuOpen)}>
          â˜°
        </div>
      </nav>

      {/* Hero Section */}
      <section className="landing-page">
        <div className="hero">
          <div className="hero-text">
            <h2>Track<br />Transform<br />Thrive</h2>
            <p>Your personalized path to better health with AI-powered insights.</p>
            <div className="hero-buttons">
              <button className="start-button" onClick={() => navigate("/login")}>GET STARTED</button>
              <button className="learn-button" onClick={() => scrollToSection(aboutRef)}>LEARN MORE</button>
            </div>
          </div>
          <div className="hero-image">
            <img src={watchImage} alt="Smartwatch" />
          </div>
        </div>
      </section>

      {/* About Section */}
      <section ref={aboutRef} className="about-section">
        <AboutPage />
      </section>

      {/* Team Section */}
      <section ref={teamRef} className="team-section">
        <TeamPage />
      </section>
    </div>
  );
};

export default LandingPage;
