import React, { useState } from "react";
import "./ForgotPassword.css";
import image from "./image 1.png"; // Use the actual image path

const ForgotPassword = () => {
  const [email, setEmail] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Reset link sent to:", email);
    // Implement email validation and API call
  };

  return (
    <div className="forgot-container">
      <div className="background"></div>
      <div className="forgot-box">
        {/* Left Side - Form */}
        <div className="forgot-form">
          <h2>Forgot Password?</h2>
          <p>Email</p>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <button className="forgot-btn" onClick={handleSubmit}>
            Reset Password
          </button>
          <p className="login-text">
            Remember Password? <a href="/signin">Login here.</a>
          </p>
        </div>

        {/* Right Side - Image */}
        <div className="forgot-image">
          <img src={image} alt="Reset Password" />
        </div>
      </div>
    </div>
  );
};

export default ForgotPassword;
