import React, { useRef } from "react";
import { Link } from "react-router-dom";
import AboutPage from "./About";
import TeamPage from "./Ourteam";
import "./LandingPage.css";  
import "./theme.css";
import watchImage from "./watch-image.png"; 

const LandingPage = () => {
  const aboutRef = useRef(null);
  const teamRef = useRef(null);
  const [menuOpen, setMenuOpen] = useState(false);

  const scrollToSection = (ref) => {
    window.scrollTo({
      top: ref.current.offsetTop,
      behavior: "smooth",
    });
  };

  return (
    <div className="landing-container"> {/* Apply full-page container */}
      <div className="background"></div>
      {/* Navbar */}
      <nav className="navbar">
        <h2 className="logo">trackfit.</h2>
        <button className="menu-icon" onClick={() => setMenuOpen(!menuOpen)}>
          â˜°
        </button>
        <div className={`nav-links ${menuOpen ? "open" : ""}`}>
          <button onClick={() => scrollToSection(aboutRef)}>About</button>
          <button onClick={() => scrollToSection(teamRef)}>Our Team</button>
          <Link to="/signin" className="login-link">Login</Link> 
        </div>
      </nav>

      {/* Landing Section */}
      <section className="landing-page">
        <div className="hero">
          <div className="hero-text">
            <h2>Track<br />Transform<br />Thrive</h2>
            <p>Your personalized path to better health with AI-powered insights.</p>
            <div className="hero-buttons">
              <Link to="/signup">
                <button className="start-button">GET STARTED</button>
              </Link>
              <button className="learn-button" onClick={() => scrollToSection(aboutRef)}>LEARN MORE</button>
            </div>
          </div>
          <div className="hero-image">
            <img src={watchImage} alt="Smartwatch" />
          </div>
        </div>
      </section>

      {/* About Section */}
      <section ref={aboutRef} id="about-section" className="about-section">
        <AboutPage />
      </section>

      {/* Team Section */}
      <section ref={teamRef} className="team-section">
        <TeamPage />
      </section>
    </div>
  );
};

export default LandingPage;
